$:/GiscusMacro.text:
\define giscus()
<$set name="theme" filter="[[$:/config/giscus/theme]!field:text[custom]get[text]]" emptyValue={{$:/config/giscus/theme/custom-theme-url}}>
<giscus-widget
  id="comments"
  repo={{$:/config/giscus/repository}}
  repoid={{$:/config/giscus/repository-id}}
  category="Announcements"
  categoryid={{$:/config/giscus/discussion-category-id}}
  mapping="specific"
  term=<<currentTiddler>>
  reactionsenabled={{$:/config/giscus/reactions}}
  emitmetadata={{$:/config/giscus/metadata}}
  inputposition={{$:/config/giscus/position}}
  theme=<<theme>>
  lang={{$:/config/giscus/lang}}
  loading={{$:/config/giscus/loading}}
></giscus-widget>
</$set>
\end

$:/plugins/abraham/giscus-for-tiddlywiki.text:
/*Desktop layout*/
@media (min-width: 960px) {
    .giscus-comment-btn {
        float: right;
        width: inherit;
    }
}

/*Mobile layout*/
@media (max-width: 959px) {
    .giscus-comment-btn {
        float: none;
        height: 35px;
        width: 100%;
    }
}

.disable-giscus-comments {
    display: none;
}
